{

dot((i,j));

}

draw((0,0)--(1,0)--(1,1)--(0,1)--cycle);
draw((3,0)--(5,0)--(5,2)--(3,2)--cycle);
draw((7,1)--(8,2)--(9,1)--(8,0)--cycle); [/asy]

